CREATE TABLE Utenti
(
    id INT IDENTITY(1,1) PRIMARY KEY,
    nome VARCHAR(40),
    cognome VARCHAR(40),
    dob DATE,
    indirizzo VARCHAR(100),
    userAdmin BIT,
    username VARCHAR(100),
    psw VARCHAR(100)
)



CREATE TABLE inPrestito
(
    id INT IDENTITY(1,1) PRIMARY KEY,
    dataInizio DATE,
    dataFine DATE,
    idUtente INT FOREIGN KEY REFERENCES Utenti(id)
    ON UPDATE CASCADE
    ON DELETE CASCADE,
    idLibro INT FOREIGN KEY REFERENCES Libri(id)
    ON UPDATE CASCADE
    ON DELETE CASCADE
)
